        private void InitializeVariables()
        {
            BoardMap = new byte[9];
            aiMoves = new byte[10];
            nextchar = "X";
            turn = 1;
            winner = 0;
            AIplayer = 1;
            Box1.Text = " ";
            Box2.Text = " ";
            Box3.Text = " ";
            Box4.Text = " ";
            Box5.Text = " ";
            Box6.Text = " ";
            Box7.Text = " ";
            Box8.Text = " ";
            Box9.Text = " ";
            Message1.Text = "Click on any Box...";
            gameCount++;
            Message4.Text = string.Concat("Game Count: ", gameCount.ToString());
            for (int i = 0; i < 9; i++)
            {
                BoardMap[i] = 0;
            }

            if (AIplayer == 1)
                AImove();
        }

        private void TurnChange()
        {
            turn++;
            if (turn % 2 == 0)
            {
                nextchar = "O";
            }
            else
            {
                nextchar = "X";
            }
            checkEnd();

            if (AIplayer == 1 && turn % 2 != 0)
                AImove();
            else if (AIplayer == 2 && turn % 2 == 0)
                AImove();
        }

        private void checkEnd()
        {
            if(BoardMap[0] == BoardMap[1] && BoardMap[0] == BoardMap[2] && BoardMap[0] != 0)
            {
                winner = BoardMap[0];
            }
            else if (BoardMap[3] == BoardMap[4] && BoardMap[4] == BoardMap[5] && BoardMap[3] != 0)
            {
                winner = BoardMap[3];
            }
            else if (BoardMap[6] == BoardMap[7] && BoardMap[7] == BoardMap[8] && BoardMap[6] != 0)
            {
                winner = BoardMap[8];
            }
            else if (BoardMap[0] == BoardMap[3] && BoardMap[3] == BoardMap[6] && BoardMap[0] != 0)
            {
                winner = BoardMap[6];
            }
            else if (BoardMap[1] == BoardMap[4] && BoardMap[4] == BoardMap[7] && BoardMap[1] != 0)
            {
                winner = BoardMap[7];
            }
            else if (BoardMap[2] == BoardMap[5] && BoardMap[5] == BoardMap[8] && BoardMap[2] != 0)
            {
                winner = BoardMap[8];
            }
            else if (BoardMap[0] == BoardMap[4] && BoardMap[4] == BoardMap[8] && BoardMap[0] != 0)
            {
                winner = BoardMap[8];
            }
            else if (BoardMap[2] == BoardMap[4] && BoardMap[4] == BoardMap[6] && BoardMap[2] != 0)
            {
                winner = BoardMap[6];
            }

            if(winner != 0)
            {
                if (winner == 1)
                    Message1.Text = "X Wins";
                else
                    Message1.Text = "O Wins";

                for (int i = 0; i < 9; i++)
                {
                    BoardMap[i] = 3; //To prevent further gameplay
                }
            }
            else if(turn > 9)
                Message1.Text = "Game Draws";
        }

        private void AImove()
        {
            System.Random r = new System.Random();
            byte move = 10, tempbyte1 = 0;
            int temp1 = 0;

            move = winMove(AIplayer); //Check if AI is winning
            if (move != 10)
                goto makemove;

            if (AIplayer == 1)
                tempbyte1 = 2;
            else
                tempbyte1 = 1;

            move = winMove(tempbyte1); //Check if player is winning
            if (move != 10)
                goto makemove;

            if (AIplayer == 1 && turn == 1)
            {
                temp1 = r.Next(4);
                switch (temp1)
                {
                    case 0: move = 0; break;
                    case 1: move = 2; break;
                    case 2: move = 6; break;
                    case 3: move = 8; break;
                    default: Message1.Text = "Random num failure"; break;
                }
            }
            else if (AIplayer == 1 && turn == 3)
            {
                if (BoardMap[4] == 2)
                {
                    if (BoardMap[0] == 1)
                        move = 8;
                    else if (BoardMap[2] == 1)
                        move = 6;
                    else if (BoardMap[6] == 1)
                        move = 2;
                    else if (BoardMap[8] == 1)
                        move = 0;
                }
                else if (BoardMap[0] == 1)
                {
                    if (BoardMap[3] == 0)
                        move = 6;
                    else
                        move = 2;
                }
                else if (BoardMap[2] == 1)
                {
                    if (BoardMap[1] == 0)
                        move = 0;
                    else
                        move = 8;
                }
                else if (BoardMap[6] == 1)
                {
                    if (BoardMap[7] == 0)
                        move = 8;
                    else
                        move = 0;
                }
                else if (BoardMap[8] == 1)
                {
                    if (BoardMap[5] == 0)
                        move = 2;
                    else
                        move = 6;
                }
            }
            else if (AIplayer == 1 && turn == 5)
            {
                if (BoardMap[4] == 0)
                {
                    switch (aiMoves[1])
                    {
                        case 0:
                            if (BoardMap[8] == 0)
                                move = 8;
                            else if(BoardMap[6] == 0)
                                move = 6;
                            break;
                        case 2:
                            if (BoardMap[6] == 0)
                                move = 6;
                            else if (BoardMap[8] == 0)
                                move = 8;
                            break;
                        case 8:
                            if (BoardMap[0] == 0)
                                move = 0;
                            else if (BoardMap[2] == 0)
                                move = 2;
                            break;
                        case 6:
                            if (BoardMap[2] == 0)
                                move = 2;
                            else if (BoardMap[0] == 0)
                                move = 0;
                            break;
                    }
                }
            }

            makemove:
            if (move < 9)
            {
                BoardMap[move] = AIplayer;
                aiMoves[turn] = move;
            }

            switch(move)
            {
                case 0: Box1.Text = nextchar; this.Box1.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 1: Box2.Text = nextchar; this.Box2.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 2: Box3.Text = nextchar; this.Box3.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 3: Box4.Text = nextchar; this.Box4.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 4: Box5.Text = nextchar; this.Box5.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 5: Box6.Text = nextchar; this.Box6.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 6: Box7.Text = nextchar; this.Box7.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 7: Box8.Text = nextchar; this.Box8.ForeColor = System.Drawing.SystemColors.ControlText; break;
                case 8: Box9.Text = nextchar; this.Box9.ForeColor = System.Drawing.SystemColors.ControlText; break;
                default: Message1.Text = "AI move failure"; break;
            }
            TurnChange();
        }

        private byte winMove( byte player )
        {
            byte move = 10, movetemp;
            byte i, j, sump, sumb;

            for(i=0;i<=6;i+=3) //Check horizontal rows
            {
                sump = 0;
                sumb = 0;
                movetemp = 10;
                for(j=i;j<=(i+2);j++)
                {
                    if (BoardMap[j] == player)
                        sump++;
                    else if (BoardMap[j] == 0)
                    {
                        sumb++;
                        movetemp = j;
                    }
                }
                if(sump==2 && sumb==1)
                {
                    move = movetemp;
                    return move;
                }
            }

            for (i = 0; i <= 2; i++) //Check Vertical Columns
            {
                sump = 0;
                sumb = 0;
                movetemp = 10;
                for (j = i; j <= (i + 6); j+=3)
                {
                    if (BoardMap[j] == player)
                        sump++;
                    else if (BoardMap[j] == 0)
                    {
                        sumb++;
                        movetemp = j;
                    }
                }
                if (sump == 2 && sumb == 1)
                {
                    move = movetemp;
                    return move;
                }
            }

            sump = 0;
            sumb = 0;
            movetemp = 10;
            for (j = 0; j <= 8; j += 4) //Check diagonal 1
            {
                if (BoardMap[j] == player)
                    sump++;
                else if (BoardMap[j] == 0)
                {
                    sumb++;
                    movetemp = j;
                }
            }
            if (sump == 2 && sumb == 1)
            {
                move = movetemp;
                return move;
            }

            sump = 0;
            sumb = 0;
            movetemp = 10;
            for (j = 2; j <= 6; j += 2) //Check diagonal 2
            {
                if (BoardMap[j] == player)
                    sump++;
                else if (BoardMap[j] == 0)
                {
                    sumb++;
                    movetemp = j;
                }
            }
            if (sump == 2 && sumb == 1)
            {
                move = movetemp;
                return move;
            }

            return move;
        }

